import CardList from "./components/CardList.vue";
import PartyDb from './database/PartyDb.json';
export default {
  name: 'App',
  components: {
    CardList
  },

  data() {
    return {
      data: PartyDb,
      search: ''
    };
  },

  computed: {
    SearchPartyField: function () {
      return this.data.filter(party => {
        return party.Nome.toLowerCase().match(this.search.toLowerCase()) || party.Collocazione.toLowerCase().match(this.search.toLowerCase());
      });
    }
  }
};
